import { state } from "./state";
import { TYPE_USER } from "./type";
 const reducer=(data=state,action)=>{
const temp={...data}

switch(action.type){

    case TYPE_USER:
        temp.user=action.value
        break;
        default:
            break;
}
return temp
}

export default reducer
